pragma solidity >=0.6.0 <0.9.0;

/**
 * @title Fallback
 * @dev Implements delegation of calls to other contracts, with proper
 * forwarding of return values and bubbling of failures.
 * It defines a fallback function that delegates all calls to the address
 * returned by the abstract _implementation() internal function.
 */
abstract contract Fallback {
 /**
 * @dev Fallback function.
 * Implemented entirely in `_fallback`.
 */
 fallback() external payable {
 _fallback();
 }

 /**
 * @dev Receive function.
 * Implemented entirely in `_fallback`.
 */
 receive() external payable {
 _fallback();
 }

 /**
 * @return The Address of the implementation.
 */
 function _implementation() internal view virtual returns (address);

 /**
 * @dev Delegates execution to an implementation contract.
 * This is a low level function that doesn't return to its internal call site.
 * It will return to the external caller whatever the implementation returns.
 * @param implementation Address to delegate.
 */
 function _delegate(address implementation) internal {
 assembly {
 // Copy msg.data. We take full control of memory in this inline assembly
 // block because it will not return to Solidity code. We overwrite the
 // Solidity scratch pad at memory position 0.
 calldatacopy(0, 0, calldatasize())

 // Call the implementation.
 // out and outsize are 0 because we don't know the size yet.
 let result := delegatecall(gas(), implementation, 0, calldatasize(), 0, 0)

 // Copy the returned data.
 returndatacopy(0, 0, returndatasize())

 switch result
 // delegatecall returns 0 on error.
 case 0 {
 revert(0, returndatasize())
 }
 default {
 return(0, returndatasize())
 }
 }
 }

 /**
 * @dev Function that is run as the first thing in the fallback function.
 * Can be redefined in derived contracts to add functionality.
 * Redefinitions must call super._willFallback().
 */
 function _willFallback() internal virtual {}

 /**
 * @dev fallback implementation.
 * Extracted to enable manual triggering.
 */
 function _fallback() internal {
 _willFallback();
 _delegate(_implementation());
 }
}

/**
 * @dev Collection of functions related to the address type
 */
library Address {
 /**
 * @dev Returns true if `account` is a contract.
 *
 * [IMPORTANT]
 * ====
 * It is unsafe to assume that an address for which this function returns
 * false is an externally-owned account (EOA) and not a contract.
 *
 * Among others, `isContract` will return false for the following
 * types of addresses:
 *
 * - an externally-owned account
 * - a contract in construction
 * - an address where a contract will be created
 * - an address where a contract lived, but was destroyed
 * ====
 */
 function isContract(address account) internal view returns (bool) {
 // This method relies in extcodesize, which returns 0 for contracts in
 // construction, since the code is only stored at the end of the
 // constructor execution.

 uint256 size;
 // solhint-disable-next-line no-inline-assembly
 assembly {
 size := extcodesize(account)
 }
 return size > 0;
 }

 /**
 * @dev Replacement for Solidity's `transfer`: sends `amount` wei to
 * `recipient`, forwarding all available gas and reverting on errors.
 *
 * https://eips.ethereum.org/EIPS/eip-1884[EIP1884] increases the gas cost
 * of certain opcodes, possibly making contracts go over the 2300 gas limit
 * imposed by `transfer`, making them unable to receive funds via
 * `transfer`. {sendValue} removes this limitation.
 *
 * https://diligence.consensys.net/posts/2019/09/stop-using-soliditys-transfer-now/[Learn more].
 *
 * IMPORTANT: because control is transferred to `recipient`, care must be
 * taken to not create reentrancy vulnerabilities. Consider using
 * {ReentrancyGuard} or the
 * https://solidity.readthedocs.io/en/v0.5.11/security-considerations.html#use-the-checks-effects-interactions-pattern[checks-effects-interactions pattern].
 */
 function sendValue(address payable recipient, uint256 amount) internal {
 require(address(this).balance >= amount, "Address: insufficient balance");

 // solhint-disable-next-line avoid-low-level-calls, avoid-call-value
 (bool success, ) = recipient.call{value: amount}("");
 require(success, "Address: unable to send value, recipient may have reverted");
 }

 /**
 * @dev Performs a Solidity function call using a low level `call`. A
 * plain`call` is an unsafe replacement for a function call: use this
 * function instead.
 *
 * If `target` reverts with a revert reason, it is bubbled up by this
 * function (like regular Solidity function calls).
 *
 * Returns the raw returned data. To convert to the expected return value,
 * use https://solidity.readthedocs.io/en/latest/units-and-global-variables.html?highlight=abi.decode#abi-encoding-and-decoding-functions[`abi.decode`].
 *
 * Requirements:
 *
 * - `target` must be a contract.
 * - calling `target` with `data` must not revert.
 *
 * _Available since v3.1._
 */
 function functionCall(address target, bytes memory data) internal returns (bytes memory) {
 return functionCall(target, data, "Address: low-level call failed");
 }

 /**
 * @dev Same as {xref-Address-functionCall-address-bytes-}[`functionCall`], but with
 * `errorMessage` as a fallback revert reason when `target` reverts.
 *
 * _Available since v3.1._
 */
 function functionCall(
 address target,
 bytes memory data,
 string memory errorMessage
 ) internal returns (bytes memory) {
 return _functionCallWithValue(target, data, 0, errorMessage);
 }

 /**
 * @dev Same as {xref-Address-functionCall-address-bytes-}[`functionCall`],
 * but also transferring `value` wei to `target`.
 *
 * Requirements:
 *
 * - the calling contract must have an ETH balance of at least `value`.
 * - the called Solidity function must be `payable`.
 *
 * _Available since v3.1._
 */
 function functionCallWithValue(
 address target,
 bytes memory data,
 uint256 value
 ) internal returns (bytes memory) {
 return functionCallWithValue(target, data, value, "Address: low-level call with value failed");
 }

 /**
 * @dev Same as {xref-Address-functionCallWithValue-address-bytes-uint256-}[`functionCallWithValue`], but
 * with `errorMessage` as a fallback revert reason when `target` reverts.
 *
 * _Available since v3.1._
 */
 function functionCallWithValue(
 address target,
 bytes memory data,
 uint256 value,
 string memory errorMessage
 ) internal returns (bytes memory) {
 require(address(this).balance >= value, "Address: insufficient balance for call");
 return _functionCallWithValue(target, data, value, errorMessage);
 }

 function _functionCallWithValue(
 address target,
 bytes memory data,
 uint256 weiValue,
 string memory errorMessage
 ) private returns (bytes memory) {
 require(isContract(target), "Address: call to non-contract");

 // solhint-disable-next-line avoid-low-level-calls
 (bool success, bytes memory returndata) = target.call{value: weiValue}(data);
 if (success) {
 return returndata;
 } else {
 // Look for revert reason and bubble it up if present
 if (returndata.length > 0) {
 // The easiest way to bubble the revert reason is using memory via assembly

 // solhint-disable-next-line no-inline-assembly
 assembly {
 let returndata_size := mload(returndata)
 revert(add(32, returndata), returndata_size)
 }
 } else {
 revert(errorMessage);
 }
 }
 }
}

/**
 * @title BaseFallback
 * @dev This contract implements a proxy that allows to change the
 * implementation address to which it will delegate.
 * Such a change is called an implementation upgrade.
 */
contract BaseFallback is Fallback {
 /**
 * @dev Contract constructor.
 * @param _logic Address of the initial implementation.
 * @param _data Data to send as msg.data to the implementation to initialize the proxied contract.
 * It should include the signature and the parameters of the function to be called, as described in
 * https://solidity.readthedocs.io/en/v0.4.24/abi-spec.html#function-selector-and-argument-encoding.
 * This parameter is optional, if no data is given the initialization call to proxied contract will be skipped.
 */
 constructor(address _logic, bytes memory _data) payable {
 assert(IMPLEMENTATION_SLOT == bytes32(uint256(keccak256("eip1967.proxy.implementation")) - 1));
 _setImplementation(_logic);
 if (_data.length > 0) {
 (bool success, ) = _logic.delegatecall(_data);
 require(success);
 }
 }

 /**
 * @dev Emitted when the implementation is upgraded.
 * @param implementation Address of the new implementation.
 */
 event Upgraded(address indexed implementation);

 /**
 * @dev Storage slot with the address of the current implementation.
 * This is the keccak-256 hash of "eip1967.proxy.implementation" subtracted by 1, and is
 * validated in the constructor.
 */
 bytes32 internal constant IMPLEMENTATION_SLOT = 0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc;

 /**
 * @dev Returns the current implementation.
 * @return impl Address of the current implementation
 */
 function _implementation() internal view override returns (address impl) {
 bytes32 slot = IMPLEMENTATION_SLOT;
 assembly {
 impl := sload(slot)
 }
 }

 /**
 * @dev Upgrades the to a new implementation.
 * @param newImplementation Address of the new implementation.
 */
 function _upgradeTo(address newImplementation) internal {
 _setImplementation(newImplementation);
 emit Upgraded(newImplementation);
 }

 /**
 * @dev Sets the implementation address of the.
 * @param newImplementation Address of the new implementation.
 */
 function _setImplementation(address newImplementation) internal {
 require(Address.isContract(newImplementation), "Cannot set a implementation to a non-contract address");

 bytes32 slot = IMPLEMENTATION_SLOT;

 assembly {
 sstore(slot, newImplementation)
 }
 }
}

/**
 * @title FarmChef
 * @dev This contract combines an upgradeability with an authorization
 * mechanism for administrative tasks.
 * All external functions in this contract must be guarded by the
 * `ifAdmin` modifier. See ethereum/solidity#3864 for a Solidity
 * feature proposal that would enable this to be done automatically.
 */
contract FarmChef is BaseFallback {
 /**
 * Contract constructor.
 * @param _logic address of the initial implementation.
 * @param _admin_ Address of the administrator.
 * @param _data Data to send as msg.data to the implementation to initialize the proxied contract.
 * It should include the signature and the parameters of the function to be called, as described in
 * https://solidity.readthedocs.io/en/v0.4.24/abi-spec.html#function-selector-and-argument-encoding.
 * This parameter is optional, if no data is given the initialization call to proxied contract will be skipped.
 */
 constructor(
 address _logic,
 address _admin_,
 bytes memory _data
 ) payable BaseFallback(_logic, _data) {
 assert(ADMIN_SLOT == bytes32(uint256(keccak256("eip1967.proxy.admin")) - 1));
 _setAdmin(_admin_);
 }

 /**
 * @dev Emitted when the administration has been transferred.
 * @param previousAdmin Address of the previous admin.
 * @param newAdmin Address of the new admin.
 */
 event AdminChanged(address previousAdmin, address newAdmin);

 /**
 * @dev Storage slot with the admin of the contract.
 * This is the keccak-256 hash of "eip1967.proxy.admin" subtracted by 1, and is
 * validated in the constructor.
 */

 bytes32 internal constant ADMIN_SLOT = 0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103;

 /**
 * @dev Modifier to check whether the `msg.sender` is the admin.
 * If it is, it will run the function. Otherwise, it will delegate the call
 * to the implementation.
 */
 modifier ifAdmin() {
 if (msg.sender == _admin()) {
 _;
 } else {
 _fallback();
 }
 }

 /**
 * @return addr The address of the admin.
 */
 function admin() external ifAdmin returns (address addr) {
 addr = _admin();
 }

 /**
 * @return addr The address of the implementation.
 */
 function implementation() external ifAdmin returns (address addr) {
 addr = _implementation();
 }

 /**
 * @dev Changes the admin of the.
 * Only the current admin can call this function.
 * @param newAdmin Address to transfer administration to.
 */
 function changeAdmin(address newAdmin) external ifAdmin {
 require(newAdmin != address(0), "Cannot change the admin of a proxy to the zero address");
 emit AdminChanged(_admin(), newAdmin);
 _setAdmin(newAdmin);
 }

 /**
 * @dev Upgrade the backing implementation of the proxy.
 * Only the admin can call this function.
 * @param newImplementation Address of the new implementation.
 */
 function upgradeTo(address newImplementation) external ifAdmin {
 _upgradeTo(newImplementation);
 }

 /**
 * @dev Upgrade the backing implementation of the proxy and call a function
 * on the new implementation.
 * This is useful to initialize the proxied contract.
 * @param newImplementation Address of the new implementation.
 * @param data Data to send as msg.data in the low level call.
 * It should include the signature and the parameters of the function to be called, as described in
 * https://solidity.readthedocs.io/en/v0.4.24/abi-spec.html#function-selector-and-argument-encoding.
 */
 function upgradeToAndCall(address newImplementation, bytes calldata data) external payable ifAdmin {
 _upgradeTo(newImplementation);
 (bool success, ) = newImplementation.delegatecall(data);
 require(success);
 }

 /**
 * @return adm The admin slot.
 */
 function _admin() internal view returns (address adm) {
 bytes32 slot = ADMIN_SLOT;
 assembly {
 adm := sload(slot)
 }
 }

 /**
 * @dev Sets the address of the proxy admin.
 * @param newAdmin Address of the new proxy admin.
 */
 function _setAdmin(address newAdmin) internal {
 bytes32 slot = ADMIN_SLOT;

 assembly {
 sstore(slot, newAdmin)
 }
 }

 /**
 * @dev Only fall back when the sender is not the admin.
 */
 function _willFallback() internal virtual override {
 require(msg.sender != _admin(), "Cannot call fallback function from the proxy admin");
 super._willFallback();
 }
}

pragma solidity >=0.6.0 <0.9.0;

// SushiToken with Governance.
contract SushiToken is ERC20("ShareSwap", "SHS"), Ownable {
 using SafeMath for uint256;
 /// @notice Creates `_amount` token to `_to`. Must only be called by the owner (MasterChef).
 function mint(address _to, uint256 _amount) public onlyOwner {
 _mint(_to, _amount);
 _moveDelegates(address(0), _delegates[_to], _amount);
 }

 // Copied and modified from YAM code:
 // https://github.com/yam-finance/yam-protocol/blob/master/contracts/token/YAMGovernanceStorage.sol
 // https://github.com/yam-finance/yam-protocol/blob/master/contracts/token/YAMGovernance.sol
 // Which is copied and modified from COMPOUND:
 // https://github.com/compound-finance/compound-protocol/blob/master/contracts/Governance/Comp.sol

 mapping (address => address) internal _delegates;

 /// @notice A checkpoint for marking number of votes from a given block
 struct Checkpoint {
 uint32 fromBlock;
 uint256 votes;
 }

 /// @notice A record of votes checkpoints for each account, by index
 mapping (address => mapping (uint32 => Checkpoint)) public checkpoints;

 /// @notice The number of checkpoints for each account
 mapping (address => uint32) public numCheckpoints;

 /// @notice The EIP-712 typehash for the contract's domain
 bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)");

 /// @notice The EIP-712 typehash for the delegation struct used by the contract
 bytes32 public constant DELEGATION_TYPEHASH = keccak256("Delegation(address delegatee,uint256 nonce,uint256 expiry)");

 /// @notice A record of states for signing / validating signatures
 mapping (address => uint) public nonces;

 /// @notice An event thats emitted when an account changes its delegate
 event DelegateChanged(address indexed delegator, address indexed fromDelegate, address indexed toDelegate);

 /// @notice An event thats emitted when a delegate account's vote balance changes
 event DelegateVotesChanged(address indexed delegate, uint previousBalance, uint newBalance);

 /**
 * @notice Delegate votes from `msg.sender` to `delegatee`
 * @param delegator The address to get delegatee for
 */
 function delegates(address delegator)
 external
 view
 returns (address)
 {
 return _delegates[delegator];
 }

 /**
 * @notice Delegate votes from `msg.sender` to `delegatee`
 * @param delegatee The address to delegate votes to
 */
 function delegate(address delegatee) external {
 return _delegate(msg.sender, delegatee);
 }

 /**
 * @notice Delegates votes from signatory to `delegatee`
 * @param delegatee The address to delegate votes to
 * @param nonce The contract state required to match the signature
 * @param expiry The time at which to expire the signature
 * @param v The recovery byte of the signature
 * @param r Half of the ECDSA signature pair
 * @param s Half of the ECDSA signature pair
 */
 function delegateBySig(
 address delegatee,
 uint nonce,
 uint expiry,
 uint8 v,
 bytes32 r,
 bytes32 s
 )
 external
 {
 bytes32 domainSeparator = keccak256(
 abi.encode(
 DOMAIN_TYPEHASH,
 keccak256(bytes(name())),
 getChainId(),
 address(this)
 )
 );

 bytes32 structHash = keccak256(
 abi.encode(
 DELEGATION_TYPEHASH,
 delegatee,
 nonce,
 expiry
 )
 );

 bytes32 digest = keccak256(
 abi.encodePacked(
 "\x19\x01",
 domainSeparator,
 structHash
 )
 );

 address signatory = ecrecover(digest, v, r, s);
 require(signatory != address(0), "SUSHI::delegateBySig: invalid signature");
 require(nonce == nonces[signatory]++, "SUSHI::delegateBySig: invalid nonce");
 require(block.timestamp <= expiry, "SUSHI::delegateBySig: signature expired");
 return _delegate(signatory, delegatee);
 }

 /**
 * @notice Gets the current votes balance for `account`
 * @param account The address to get votes balance
 * @return The number of current votes for `account`
 */
 function getCurrentVotes(address account)
 external
 view
 returns (uint256)
 {
 uint32 nCheckpoints = numCheckpoints[account];
 return nCheckpoints > 0 ? checkpoints[account][nCheckpoints - 1].votes : 0;
 }

 /**
 * @notice Determine the prior number of votes for an account as of a block number
 * @dev Block number must be a finalized block or else this function will revert to prevent misinformation.
 * @param account The address of the account to check
 * @param blockNumber The block number to get the vote balance at
 * @return The number of votes the account had as of the given block
 */
 function getPriorVotes(address account, uint blockNumber)
 external
 view
 returns (uint256)
 {
 require(blockNumber < block.number, "SUSHI::getPriorVotes: not yet determined");

 uint32 nCheckpoints = numCheckpoints[account];
 if (nCheckpoints == 0) {
 return 0;
 }

 // First check most recent balance
 if (checkpoints[account][nCheckpoints - 1].fromBlock <= blockNumber) {
 return checkpoints[account][nCheckpoints - 1].votes;
 }

 // Next check implicit zero balance
 if (checkpoints[account][0].fromBlock > blockNumber) {
 return 0;
 }

 uint32 lower = 0;
 uint32 upper = nCheckpoints - 1;
 while (upper > lower) {
 uint32 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
 Checkpoint memory cp = checkpoints[account][center];
 if (cp.fromBlock == blockNumber) {
 return cp.votes;
 } else if (cp.fromBlock < blockNumber) {
 lower = center;
 } else {
 upper = center - 1;
 }
 }
 return checkpoints[account][lower].votes;
 }

 function _delegate(address delegator, address delegatee)
 internal
 {
 address currentDelegate = _delegates[delegator];
 uint256 delegatorBalance = balanceOf(delegator); // balance of underlying SUSHIs (not scaled);
 _delegates[delegator] = delegatee;

 emit DelegateChanged(delegator, currentDelegate, delegatee);

 _moveDelegates(currentDelegate, delegatee, delegatorBalance);
 }

 function _moveDelegates(address srcRep, address dstRep, uint256 amount) internal {
 if (srcRep != dstRep && amount > 0) {
 if (srcRep != address(0)) {
 // decrease old representative
 uint32 srcRepNum = numCheckpoints[srcRep];
 uint256 srcRepOld = srcRepNum > 0 ? checkpoints[srcRep][srcRepNum - 1].votes : 0;
 uint256 srcRepNew = srcRepOld.sub(amount);
 _writeCheckpoint(srcRep, srcRepNum, srcRepOld, srcRepNew);
 }

 if (dstRep != address(0)) {
 // increase new representative
 uint32 dstRepNum = numCheckpoints[dstRep];
 uint256 dstRepOld = dstRepNum > 0 ? checkpoints[dstRep][dstRepNum - 1].votes : 0;
 uint256 dstRepNew = dstRepOld.add(amount);
 _writeCheckpoint(dstRep, dstRepNum, dstRepOld, dstRepNew);
 }
 }
 }

 function _writeCheckpoint(
 address delegatee,
 uint32 nCheckpoints,
 uint256 oldVotes,
 uint256 newVotes
 )
 internal
 {
 uint32 blockNumber = safe32(block.number, "SUSHI::_writeCheckpoint: block number exceeds 32 bits");

 if (nCheckpoints > 0 && checkpoints[delegatee][nCheckpoints - 1].fromBlock == blockNumber) {
 checkpoints[delegatee][nCheckpoints - 1].votes = newVotes;
 } else {
 checkpoints[delegatee][nCheckpoints] = Checkpoint(blockNumber, newVotes);
 numCheckpoints[delegatee] = nCheckpoints + 1;
 }

 emit DelegateVotesChanged(delegatee, oldVotes, newVotes);
 }

 function safe32(uint n, string memory errorMessage) internal pure returns (uint32) {
 require(n < 2**32, errorMessage);
 return uint32(n);
 }

 function getChainId() internal view returns (uint) {
 return block.chainid;
 }
}


pragma solidity >=0.6.0 <0.9.0;

contract MasterChef is Ownable {
 using SafeMath for uint256;
 using SafeERC20 for IERC20;

 // Info of each user.
 struct UserInfo {
 uint256 amount; // How many LP tokens the user has provided.
 uint256 rewardDebt; // Reward debt. See explanation below.
 //
 // We do some fancy math here. Basically, any point in time, the amount of SUSHIs
 // entitled to a user but is pending to be distributed is:
 //
 // pending reward = (user.amount * pool.accSushiPerShare) - user.rewardDebt
 //
 // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
 // 1. The pool's `accSushiPerShare` (and `lastRewardBlock`) gets updated.
 // 2. User receives the pending reward sent to his/her address.
 // 3. User's `amount` gets updated.
 // 4. User's `rewardDebt` gets updated.
 }

 // Info of each pool.
 struct PoolInfo {
 IERC20 lpToken; // Address of LP token contract.
 uint256 allocPoint; // How many allocation points assigned to this pool. SUSHIs to distribute per block.
 uint256 lastRewardBlock; // Last block number that SUSHIs distribution occurs.
 uint256 accSushiPerShare; // Accumulated SUSHIs per share, times 1e12. See below.
 }

 // The SUSHI TOKEN!
 SushiToken public sushi;
 // Dev address.
 address public devaddr;
 // Block number when bonus SUSHI period ends.
 uint256 public bonusEndBlock;
 // SUSHI tokens created per block.
 uint256 public sushiPerBlock;

 // Info of each pool.
 PoolInfo[] public poolInfo;
 // Info of each user that stakes LP tokens.
 mapping (uint256 => mapping (address => UserInfo)) public userInfo;
 // Total allocation poitns. Must be the sum of all allocation points in all pools.
 uint256 public totalAllocPoint = 0;
 // The block number when SUSHI mining starts.
 uint256 public startBlock;
 uint256 public constant BONUS_MULTIPLIER = 8;

 event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
 event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
 event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

 constructor(
 SushiToken _sushi,
 address _devaddr,
 uint256 _sushiPerBlock,
 uint256 _startBlock,
 uint256 _bonusEndBlock
 ) {
 sushi = _sushi;
 devaddr = _devaddr;
 sushiPerBlock = _sushiPerBlock;
 bonusEndBlock = _bonusEndBlock;
 startBlock = _startBlock;
 }

 function poolLength() external view returns (uint256) {
 return poolInfo.length;
 }

 // Add a new lp to the pool. Can only be called by the owner.
 function add(uint256 _allocPoint, IERC20 _lpToken, bool _withUpdate) public {
 if (_withUpdate) {
 massUpdatePools();
 }
 uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
 totalAllocPoint = totalAllocPoint.add(_allocPoint);
 poolInfo.push(PoolInfo({
 lpToken: _lpToken,
 allocPoint: _allocPoint,
 lastRewardBlock: lastRewardBlock,
 accSushiPerShare: 0
 }));
 }

 // Update the given pool's SUSHI allocation point. Can only be called by the owner.
 function set(uint256 _pid, uint256 _allocPoint, bool _withUpdate) public {
 if (_withUpdate) {
 massUpdatePools();
 }
 totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
 poolInfo[_pid].allocPoint = _allocPoint;
 }

 // Return reward multiplier over the given _from to _to block.
 function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
 if (_to <= bonusEndBlock) {
 return _to.sub(_from).mul(BONUS_MULTIPLIER);
 } else if (_from >= bonusEndBlock) {
 return _to.sub(_from);
 } else {
 return bonusEndBlock.sub(_from).mul(BONUS_MULTIPLIER).add(
 _to.sub(bonusEndBlock)
 );
 }
 }

 // View function to see pending SUSHIs on frontend.
 function pendingSushi(uint256 _pid, address _user) external view returns (uint256) {
 PoolInfo storage pool = poolInfo[_pid];
 UserInfo storage user = userInfo[_pid][_user];
 uint256 accSushiPerShare = pool.accSushiPerShare;
 uint256 lpSupply = pool.lpToken.balanceOf(address(this));
 if (block.number > pool.lastRewardBlock && lpSupply != 0) {
 uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
 uint256 sushiReward = multiplier.mul(sushiPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
 accSushiPerShare = accSushiPerShare.add(sushiReward.mul(1e12).div(lpSupply));
 }
 return user.amount.mul(accSushiPerShare).div(1e12).sub(user.rewardDebt);
 }

 // Update reward variables for all pools. Be careful of gas spending!
 function massUpdatePools() public {
 uint256 length = poolInfo.length;
 for (uint256 pid = 0; pid < length; ++pid) {
 updatePool(pid);
 }
 }

 // Update reward variables of the given pool to be up-to-date.
 function updatePool(uint256 _pid) public {
 PoolInfo storage pool = poolInfo[_pid];
 if (block.number <= pool.lastRewardBlock) {
 return;
 }
 uint256 lpSupply = pool.lpToken.balanceOf(address(this));
 if (lpSupply == 0) {
 pool.lastRewardBlock = block.number;
 return;
 }
 uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
 uint256 sushiReward = multiplier.mul(sushiPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
 pool.accSushiPerShare = pool.accSushiPerShare.add(sushiReward.mul(1e12).div(lpSupply));
 pool.lastRewardBlock = block.number;
 }

 // Deposit LP tokens to MasterChef for SUSHI allocation.
 function deposit(uint256 _pid, uint256 _amount) public {
 PoolInfo storage pool = poolInfo[_pid];
 UserInfo storage user = userInfo[_pid][msg.sender];
 updatePool(_pid);
 if (user.amount > 0) {
 uint256 pending = user.amount.mul(pool.accSushiPerShare).div(1e12).sub(user.rewardDebt);
 if(pending > 0) {
 safeSushiTransfer(msg.sender, pending);
 }
 }
 if(_amount > 0) {
 pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
 user.amount = user.amount.add(_amount);
 }
 user.rewardDebt = user.amount.mul(pool.accSushiPerShare).div(1e12);
 emit Deposit(msg.sender, _pid, _amount);
 }

 // Withdraw LP tokens from MasterChef.
 function withdraw(uint256 _pid, uint256 _amount) public {
 PoolInfo storage pool = poolInfo[_pid];
 UserInfo storage user = userInfo[_pid][msg.sender];
 require(user.amount >= _amount, "withdraw: not good");
 updatePool(_pid);
 uint256 pending = user.amount.mul(pool.accSushiPerShare).div(1e12).sub(user.rewardDebt);
 if(pending > 0) {
 safeSushiTransfer(msg.sender, pending);
 }
 if(_amount > 0) {
 user.amount = user.amount.sub(_amount);

 // pid0 token, pid1 pair 0% fee, other token 5% fee
 uint256 buybackAmount = _amount.mul(50).div(1000);
 // transfer to treasury contract and buyback token.
 if (_pid > 1 && buybackAmount > 0) {
 pool.lpToken.safeTransfer(devaddr, buybackAmount);
 _amount = _amount.sub(buybackAmount);
 }
 pool.lpToken.safeTransfer(address(msg.sender), _amount);
 }
 user.rewardDebt = user.amount.mul(pool.accSushiPerShare).div(1e12);
 emit Withdraw(msg.sender, _pid, _amount);
 }

 // Withdraw without caring about rewards. EMERGENCY ONLY.
 function emergencyWithdraw(uint256 _pid) public {
 PoolInfo storage pool = poolInfo[_pid];
 UserInfo storage user = userInfo[_pid][msg.sender];
 pool.lpToken.safeTransfer(address(msg.sender), user.amount);
 emit EmergencyWithdraw(msg.sender, _pid, user.amount);
 user.amount = 0;
 user.rewardDebt = 0;
 }

 // Safe sushi transfer function, just in case if rounding error causes pool to not have enough SUSHIs.
 function safeSushiTransfer(address _to, uint256 _amount) internal {
 uint256 sushiBal = sushi.balanceOf(address(this));
 if (_amount > sushiBal) {
 sushi.transfer(_to, sushiBal);
 } else {
 sushi.transfer(_to, _amount);
 }
 }

 // Update dev address by the previous dev.
 function dev(address _devaddr) public {
 require(msg.sender == devaddr, "dev: wut?");
 devaddr = _devaddr;
 }
}

