 "/root/sukiyaki/sukiyaki-contracts/contracts/MasterChef.sol": {
 "content": "// SPDX-License-Identifier: MIT\n\n//https://sukiyakiswap.finance\n\npragma solidity 0.6.12;\n\nimport \"@openzeppelin/contracts/math/SafeMath.sol\";\nimport \"./libs/IBEP20.sol\";\nimport \"./libs/SafeBEP20.sol\";\nimport \"@openzeppelin/contracts/access/Ownable.sol\";\n\nimport \"./SukiToken.sol\";\n\ninterface IMembers {\n function addMember(address _member, address _sponsor) external;\n\n function isMember(address _member) external view returns (bool);\n\n function membersList(uint256 _id) external view returns (address);\n\n function setVenus(address _venus) external;\n\n function getParentTree(address _member, uint256 _deep) external view returns (address[] memory);\n}\n\n\n\n// MasterChef is the master of SukiToken. He can make SUKIs and he is a fair guy.\n//\n// Note that it's ownable and the owner wields tremendous power. The ownership\n// will be transferred to a governance smart contract once SUKIs is sufficiently\n// distributed and the community can show to govern itself.\n//\n// Have fun reading it. Hopefully it's bug-free. God bless.\ncontract MasterChef is Ownable {\n using SafeMath for uint256;\n using SafeBEP20 for IBEP20;\n\n // Info of each user.\n struct UserInfo {\n uint256 amount; // How many LP tokens the user has provided.\n uint256 rewardDebt; // Reward debt. See explanation below.\n //\n // We do some fancy math here. Basically, any point in time, the amount of SUKIs\n // entitled to a user but is pending to be distributed is:\n //\n // pending reward = (user.amount * pool.accEggPerShare) - user.rewardDebt\n //\n // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:\n // 1. The pool's `accEggPerShare` (and `lastRewardBlock`) gets updated.\n // 2. User receives the pending reward sent to his/her address.\n // 3. User's `amount` gets updated.\n // 4. User's `rewardDebt` gets updated.\n }\n\n // Info of each pool.\n struct PoolInfo {\n IBEP20 lpToken; // Address of LP token contract.\n uint256 allocPoint; // How many allocation points assigned to this pool. EGGs to distribute per block.\n uint256 lastRewardBlock; // Last block number that EGGs distribution occurs.\n uint256 accEggPerShare; // Accumulated SUKIs per share, times 1e12. See below.\n uint16 depositFeeBP; // Deposit fee in basis points\n }\n\n // The Suki TOKEN!\n SukiToken public egg;\n // Members\n IMembers public member; \n // Dev address.\n address public devaddr;\n // Suki tokens created per block.\n uint256 public eggPerBlock;\n // Bonus muliplier for early SUKI makers.\n uint256 public constant BONUS_MULTIPLIER = 1;\n // Initial emission rate: 1 SUKI per block.\n uint256 public constant INITIAL_EMISSION_RATE = 1 ether;\n // Minimum emission rate: 0.1 SUKI per block.\n uint256 public constant MINIMUM_EMISSION_RATE = 100 finney;\n // Initial reduce emission every 14,400 blocks ~ 12 hours.\n uint256 public emissionReductionPeriodBlocks = 14400;\n // Initial emission reduction rate per period in basis points: 20%.\n uint256 public constant EMISSION_REDUCTION_RATE_PER_PERIOD = 2000;\n // Initial emission extended period per epoch in basis points: 150%.\n uint256 public constant EMISSION_EXTENDED_PERIOD_EACH_EPOCH = 1500;\n // Last reduction period index\n uint256 public lastReductionPeriodIndex = 0;\n\n // Deposit Fee address\n address public feeAddress;\n\n // Info of each pool.\n PoolInfo[] public poolInfo;\n // Info of each user that stakes LP tokens.\n mapping (uint256 => mapping (address => UserInfo)) public userInfo;\n // !!!! change userDexirius to userSuki !!!!\n mapping(address => mapping(uint256 => uint256)) public userSuki;\n\n // Total allocation points. Must be the sum of all allocation points in all pools.\n uint256 public totalAllocPoint = 0;\n // The block number when Tokens mining starts.\n uint256 public startBlock;\n uint256[5] public refPercent = [0, 0, 0, 0, 0];\n uint256[5] public refBalance = [0, 0, 0, 0, 0];\n // address public charity;\n \n event Deposit(address indexed user, uint256 indexed pid, uint256 amount);\n event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);\n event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);\n\n constructor(\n SukiToken _egg,\n address _devaddr,\n address _feeAddress,\n uint256 _startBlock,\n IMembers _member\n ) public {\n egg = _egg;\n devaddr = _devaddr;\n feeAddress = _feeAddress;\n startBlock = _startBlock;\n member = _member;\n eggPerBlock = INITIAL_EMISSION_RATE;\n }\n\n\n function poolLength() external view returns (uint256) {\n return poolInfo.length;\n }\n\n // Add a new lp to the pool. Can only be called by the owner.\n // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.\n function add(uint256 _allocPoint, IBEP20 _lpToken, uint16 _depositFeeBP, bool _withUpdate) public onlyOwner {\n require(_depositFeeBP <= 10000, \"add: invalid deposit fee basis points\");\n if (_withUpdate) {\n massUpdatePools();\n }\n uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock; \n totalAllocPoint = totalAllocPoint.add(_allocPoint);\n poolInfo.push(PoolInfo({\n lpToken: _lpToken,\n allocPoint: _allocPoint,\n lastRewardBlock: lastRewardBlock,\n accEggPerShare: 0,\n depositFeeBP: _depositFeeBP\n }));\n }\n\n // Update the given pool's EGG allocation point and deposit fee. Can only be called by the owner.\n function set(uint256 _pid, uint256 _allocPoint, uint16 _depositFeeBP, bool _withUpdate) public onlyOwner {\n require(_depositFeeBP <= 10000, \"set: invalid deposit fee basis points\");\n if (_withUpdate) {\n massUpdatePools();\n }\n totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);\n poolInfo[_pid].allocPoint = _allocPoint;\n poolInfo[_pid].depositFeeBP = _depositFeeBP;\n }\n\n // Return reward multiplier over the given _from to _to block.\n function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {\n return _to.sub(_from).mul(BONUS_MULTIPLIER);\n }\n\n // View function to see pending Tokens on frontend.\n function pendingEgg(uint256 _pid, address _user) external view returns (uint256) {\n PoolInfo storage pool = poolInfo[_pid];\n UserInfo storage user = userInfo[_pid][_user];\n uint256 accEggPerShare = pool.accEggPerShare;\n uint256 lpSupply = pool.lpToken.balanceOf(address(this));\n if (block.number > pool.lastRewardBlock && lpSupply != 0) {\n uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);\n uint256 eggReward = multiplier.mul(eggPerBlock).mul(pool.allocPoint).div(totalAllocPoint);\n accEggPerShare = accEggPerShare.add(eggReward.mul(1e12).div(lpSupply));\n }\n return user.amount.mul(accEggPerShare).div(1e12).sub(user.rewardDebt);\n }\n\n // Update reward variables for all pools. Be careful of gas spending!\n function massUpdatePools() public {\n uint256 length = poolInfo.length;\n for (uint256 pid = 0; pid < length; ++pid) {\n updatePool(pid);\n }\n }\n\n // Update reward variables of the given pool to be up-to-date.\n function updatePool(uint256 _pid) public {\n PoolInfo storage pool = poolInfo[_pid];\n if (block.number <= pool.lastRewardBlock) {\n return;\n }\n uint256 lpSupply = pool.lpToken.balanceOf(address(this));\n if (lpSupply == 0 || pool.allocPoint == 0) {\n pool.lastRewardBlock = block.number;\n return;\n }\n uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);\n uint256 eggReward = multiplier.mul(eggPerBlock).mul(pool.allocPoint).div(totalAllocPoint);\n egg.mint(devaddr, eggReward.div(10));\n // egg.mint(charity, eggReward.div(4));\n egg.mint(address(this), eggReward);\n pool.accEggPerShare = pool.accEggPerShare.add(eggReward.mul(1e12).div(lpSupply));\n pool.lastRewardBlock = block.number;\n }\n\n // Deposit LP tokens to MasterChef for EGG allocation.\n function deposit(uint256 _pid, uint256 _amount, address ref) public {\n if(member.isMember(ref) == false){\n ref = member.membersList(0);\n }\n\n if(member.isMember(msg.sender) == false){\n member.addMember(msg.sender, ref);\n } \n\n PoolInfo storage pool = poolInfo[_pid];\n UserInfo storage user = userInfo[_pid][msg.sender];\n updatePool(_pid);\n if (user.amount > 0) {\n uint256 pending = user.amount.mul(pool.accEggPerShare).div(1e12).sub(user.rewardDebt);\n if(pending > 0) {\n safeEggTransfer(msg.sender, pending);\n address[] memory refTree = member.getParentTree(msg.sender, 1);\n for (uint256 i = 0; i < 1; i++) {\n if (refTree[i] != address(0) && refPercent[i] > 0 && pending > 0) {\n uint256 refAmount = pending.mul(refPercent[i]).div(100 ether);\n if(refAmount > 0 && egg.balanceOf(refTree[i]) >= refBalance[i]){\n egg.mint(refTree[i], refAmount);\n }\n } else {\n break;\n }\n } \n }\n }\n if(_amount > 0) {\n pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);\n if(pool.depositFeeBP > 0){\n uint256 depositFee = _amount.mul(pool.depositFeeBP).div(10000);\n pool.lpToken.safeTransfer(feeAddress, depositFee);\n user.amount = user.amount.add(_amount).sub(depositFee);\n }else{\n user.amount = user.amount.add(_amount);\n }\n userSuki[msg.sender][_pid] = now;\n }\n user.rewardDebt = user.amount.mul(pool.accEggPerShare).div(1e12); \n updateEmissionRate();\n emit Deposit(msg.sender, _pid, _amount);\n }\n\n // Withdraw LP tokens from MasterChef.\n function withdraw(uint256 _pid, uint256 _amount) public {\n PoolInfo storage pool = poolInfo[_pid];\n UserInfo storage user = userInfo[_pid][msg.sender];\n require(user.amount >= _amount, \"withdraw: not good\");\n updatePool(_pid);\n uint256 pending = user.amount.mul(pool.accEggPerShare).div(1e12).sub(user.rewardDebt);\n if(pending > 0) {\n safeEggTransfer(msg.sender, pending);\n address[] memory refTree = member.getParentTree(msg.sender, 1);\n for (uint256 i = 0; i < 1; i++) {\n if (refTree[i] != address(0) && refPercent[i] > 0 && pending > 0) {\n uint256 refAmount = pending.mul(refPercent[i]).div(100 ether);\n if(refAmount > 0 && egg.balanceOf(refTree[i]) >= refBalance[i]){\n egg.mint(refTree[i], refAmount);\n }\n } else {\n break;\n }\n }\n }\n if(_amount > 0) {\n user.amount = user.amount.sub(_amount);\n pool.lpToken.safeTransfer(address(msg.sender), _amount);\n }\n user.rewardDebt = user.amount.mul(pool.accEggPerShare).div(1e12);\n updateEmissionRate();\n emit Withdraw(msg.sender, _pid, _amount);\n }\n\n // Withdraw without caring about rewards. EMERGENCY ONLY.\n function emergencyWithdraw(uint256 _pid) public {\n PoolInfo storage pool = poolInfo[_pid];\n UserInfo storage user = userInfo[_pid][msg.sender];\n uint256 amount = user.amount;\n user.amount = 0;\n user.rewardDebt = 0;\n pool.lpToken.safeTransfer(address(msg.sender), amount);\n emit EmergencyWithdraw(msg.sender, _pid, amount);\n }\n\n // Safe egg transfer function, just in case if rounding error causes pool to not have enough EGGs.\n function safeEggTransfer(address _to, uint256 _amount) internal {\n uint256 eggBal = egg.balanceOf(address(this));\n if (_amount > eggBal) {\n egg.transfer(_to, eggBal);\n } else {\n egg.transfer(_to, _amount);\n }\n }\n\n // Update dev address by the previous dev.\n function dev(address _devaddr) public {\n require(msg.sender == devaddr, \"dev: wut?\");\n devaddr = _devaddr;\n }\n\n function setFeeAddress(address _feeAddress) public{\n require(msg.sender == feeAddress, \"setFeeAddress: FORBIDDEN\");\n feeAddress = _feeAddress;\n }\n\n\n\n function updateEmissionRate() public {\n // require(block.number > startBlock, \"updateEmissionRate: Can only be called after mining starts\");\n // require(eggPerBlock > MINIMUM_EMISSION_RATE, \"updateEmissionRate: Emission rate has reached the minimum threshold\");\n if(block.number <= startBlock){\n return;\n }\n if(eggPerBlock <= MINIMUM_EMISSION_RATE){\n return;\n }\n uint256 currentIndex = block.number.sub(startBlock).div(emissionReductionPeriodBlocks);\n if (currentIndex <= lastReductionPeriodIndex) {\n return;\n }\n\n uint256 newEmissionRate = eggPerBlock;\n for (uint256 index = lastReductionPeriodIndex; index < currentIndex; ++index) {\n newEmissionRate = newEmissionRate.mul(1e4 - EMISSION_REDUCTION_RATE_PER_PERIOD).div(1e4);\n }\n\n newEmissionRate = newEmissionRate < MINIMUM_EMISSION_RATE ? MINIMUM_EMISSION_RATE : newEmissionRate;\n if (newEmissionRate >= eggPerBlock) {\n return;\n }\n massUpdatePools();\n lastReductionPeriodIndex = currentIndex;\n uint256 previousEmissionRate = eggPerBlock;\n eggPerBlock = newEmissionRate;\n emissionReductionPeriodBlocks = emissionReductionPeriodBlocks.mul(EMISSION_EXTENDED_PERIOD_EACH_EPOCH).div(1e3);\n }\n\n\n\n\n function setPercent(uint256 r_1, uint256 r_2, uint256 r_3, uint256 r_4, uint256 r_5) external onlyOwner {\n refPercent[0] = r_1;\n refPercent[1] = r_2;\n refPercent[2] = r_3;\n refPercent[3] = r_4;\n refPercent[4] = r_5;\n } \n function setRefBalance(uint256 r_1, uint256 r_2, uint256 r_3, uint256 r_4, uint256 r_5) external onlyOwner {\n refBalance[0] = r_1;\n refBalance[1] = r_2;\n refBalance[2] = r_3;\n refBalance[3] = r_4;\n refBalance[4] = r_5;\n } \n\n function register(address ref) external {\n if(member.isMember(ref) == false){\n ref = member.membersList(0);\n }\n if(member.isMember(msg.sender) == false){\n member.addMember(msg.sender, ref);\n }\n }\n\n}"
